mapper.insert:
insert into qna_board(
	qna_title, qna_userid, qna_content
) values (
	#{qna_title}, #{qna_userid}, #{qna_content}
)

mapper.update:
update qna_board 
   set qna_title = #{qna_title}, 
   	   qna_content = #{qna_content}
 where qna_no = #{qna_no}

mapper.select:
select qna_no, qna_title, qna_userid, qna_content, qna_datetime
  from qna_board 
 where qna_no = #{qna_no}
